import React from 'react';
import VideoList from './videolist';
const axios = require('axios');




export default class leftPanel extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
          user: this.props.user,
          data: {name: '', videos: []},
          isDataLoaded: 0,
        }
      }

      getLibraryContent() {
        var context = this;
        var data = {};
        var libraryURL = 'http://localhost:2999/data';
        console.log(this.state.user);
        axios.get(libraryURL, {
            params: {
                user: this.state.user,
            }
        })
        .then( function (response) {  //Problem here
            data = response.data;
            context.setState({data: data, isDataLoaded: 1});
            console.log("Data got : "+JSON.stringify(data));
            console.log("isDataLoaded : "+this.state.isDataLoaded);
        })
        .catch((err) => {
            console.log(err);
        })
    }

    componentDidMount() {
        console.log("In components did mount");
        this.setState({data: this.getLibraryContent()});
    }
    

    render(){
        console.log("In render : "+JSON.stringify(this.state.data));
        console.log("Rendering left panel with isDataLoaded: "+this.state.isDataLoaded);

            return (

                <VideoList width='33.33%' user={this.state.user} data= {this.state.data.videos} isDataLoaded={this.state.isDataLoaded}/>
    
            );


    }

};
