import initialState from '../store/initialState';


const showOnRight = (state = [], action) => {

    switch(action.type) {

        case 'SHOW_WELCOME':
            var rep = state;
            rep.rightShowWelcome= 1;
            rep = action.payload;
            console.log("Received by right reducer : "+rep);
            return rep;
        
        case 'SHOW_SEARCH_AND_RESULTS':
            var rep = state;
            rep.rightShowSearchAndResults= 1;
            rep = action.payload;
            console.log("Received by right reducer : "+rep);
            return rep;

        case 'SHOW_VIDEO_PLAYER':
            var rep = state;
            rep.rightShowVideo= 1;
            rep = action.payload;
            console.log("Received by right reducer : "+rep);
            return rep;

        default: 
            state = initialState;
            console.log("Returning by default : "+state);
            return state;
        
    }

}

export default showOnRight;
