import React from 'react';
import VideoPlayer from './videoPlayer';
import { Header } from 'semantic-ui-react';
import {showVideoPlayer} from '../actions';
import './components.css';
import 'semantic-ui-css/semantic.min.css';
import { store } from '../store';




export default class RightVideoPlayer extends React.Component {

    constructor(props) {
        super(props);
        this.updateWindowDimensions = this.updateWindowDimensions.bind(this);
        this.width = '960px';
        this.height = '540px';
        //this.thumburl = '';

    }
  
  componentDidMount() {
    this.updateWindowDimensions();
    window.addEventListener('resize', this.updateWindowDimensions);
  }
  
  componentWillUnmount() {
    window.removeEventListener('resize', this.updateWindowDimensions);
  }
  
  updateWindowDimensions() {

    console.log('Update window dimensions '+window.innerWidth+'x'+window.innerHeight);
    if(window.innerWidth < 1333) {
        this.width = '480px';
        this.height = '360px';
        store.dispatch(showVideoPlayer({
            title: this.props.video.title,
            id: this.props.video.id,
            thumbnails: this.props.video.thumbnails
        }))
        //this.thumburl = this.props.video
    }    
  }

    render()  {
        var VideoName ;
        var url;
        var thumburl;

        var video = this.props.video;

        if(video === undefined) {
            VideoName = 'Okonami no suzuki';
            url = 'https://www.youtube.com/watch?v=txkzJVZhVYc';
            thumburl = 'https://image.noelshack.com/fichiers/2020/12/5/1584732752-yagami-light.jpg';
        }
        else {
            VideoName = video.title;
            url = 'https://www.youtube.com/watch?v='+video.id;
            if((this.width === '480px') || (this.width === '960px')) {
                thumburl = video.thumbnails.high.url;
            }
            else {
                thumburl = video.thumbnails.medium.url;
            }
        }

        console.log('Showing : '+VideoName);

        return(
                <div className='videoContainer'>
                    <Header > {VideoName} </Header>
                    <VideoPlayer className = 'videoPlayer' width = {this.width} height = {this.height} url={url} thumburl={thumburl}/>
                </div>
        );

    }
}
