const showOnRight = (state = {}, action) => {

    switch(action.type) {

        case 'SHOW_WELCOME':
            var rep = {
                ...state,
                rightShowWelcome: 1
            };
            rep = action.payload;
            console.log("Received by right reducer : "+rep);
            return rep;
        
        case 'SHOW_SEARCH_AND_RESULTS':
            var rep = {
                ...state,
                rightShowSearchAndResults: 1
            }
            rep = action.payload;
            console.log("Received by right reducer : "+rep);
            return rep;

        case 'SHOW_VIDEO_PLAYER':
            var rep = {
                ...state,
                rightShowVideo: 1
            }
            rep = action.payload;
            console.log("Received by right reducer : "+rep);
            return rep;

        default: 
            return state;
        
    }

}

export default showOnRight;
