import React from 'react';
import VideoList from './videolist';
const axios = require('axios');




export default class leftPanel extends React.Component{

    constructor(props) {
        super(props);
        this.state = {
          user: this.props.user,
          data: {name: '', videos: []},
        }
      }

      getLibraryContent() {
        var context = this;
        var data = {};
        var libraryURL = 'http://localhost:2999/data';
        console.log(this.state.user);
        axios.get(libraryURL, {
            params: {
                user: this.state.user,
            }
        })
        .then( function (response) {  //Problem here
            data = response.data;
            console.log("Data got : "+JSON.stringify(data));
            context.setState({data: data});
            return data;
        })
        .catch((err) => {
            console.log(err);
        })
    }

    componentDidMount() {
        console.log("In components did mount");
        this.setState({data: this.getLibraryContent()});
    }
    

    render(){
        
        if(this.state.data !== undefined){
            return (

                <VideoList width='33.33%' user={this.state.user} data= {this.state.data.videos} />
    
            );
        }
        return(

            <VideoList width='33.33%' user={this.state.user}/>

        );

    }

};
