        <WelcomeRightPanel />

      </div>
      );
    }
    return (

      <div className="App">


        <Header className='App-header'>
          Your Youtube library
        </Header>

        <LeftPanel className= "leftPanel" user= {user}/>

      <RightPanel className = "rightPanel" user= {user}/>

      </div>

    );
  }

}
