import React from 'react';
import VideoList from './videolist';
import { Header, Image } from 'semantic-ui-react';
import Search from './search'; 
import './components.css';
import 'semantic-ui-css/semantic.min.css';
import { store} from '../store';
import {toogleIsDataLoaded, setUser} from '../actions';
const axios = require('axios');




export default class leftPanel extends React.Component{

    constructor(props) {
        super(props);
        this.data = {name: '', videos: []};
        this.getUser();
      }

      getLibraryContent() {
        var context = this;
        var data = {name: '', videos: []};
        var libraryURL = 'http://localhost:2999/data';
        console.log(store.getState().SetUser.user);
        axios.get(libraryURL, {
            params: {
                user: store.getState().SetUser.user,
            }
        })
        .then( function (response) { 
            data = response.data;
            context.data = data;
            console.log("Receiving response from server");
            context.dispatchToogleDataLoaded('leftPanel');
            //console.log("Data got : "+JSON.stringify(context.data));
            //return data;
        })
        .catch((err) => {
            console.log(err);
        })
    }

    componentDidMount(){
        var data = this.getLibraryContent();
        if(data !== undefined)
            this.data= data;
    }

    dispatchToogleDataLoaded(panel) {
        store.dispatch(toogleIsDataLoaded(panel));
      }

      getUser(){

        var regex = /http:\/\/localhost:3000\/\?user=([\w-]{1,})/;
     
        var url = window.location.href;
        var user = url.match(regex)[1];
        this.dispatchSetUser(user);
       }
     
       dispatchSetUser(user) {
         store.dispatch(setUser(user));
         console.log("User dispatch done");
       }


    

    render(){
        //console.log("In render (left): "+JSON.stringify(this.data));
        console.log("User in left Panel : "+store.getState().SetUser.user)
        console.log("Rendering left panel with isDataLoaded: "+store.getState().DataLoading.isLeftDataLoaded);




            return (
                <div className='leftPanel'>
                    <Header className='libraryName'>
                        <Image size='mini' circular src='https://image.noelshack.com/fichiers/2020/12/4/1584657095-miniytlogo.png' /> {store.getState().SetUser.user}'s library
                    </Header>
                    <Search className='search' style={{ height: '10%', width: '340px', margin: '0 auto' }}/>

                    <VideoList className='videoList' side='OnLeft' user={store.getState().SetUser.user} data= {this.data.videos} isDataLoaded={store.getState().DataLoading.isLeftDataLoaded}/>
                </div>
            );


    }

};
